		'showoptions' 	=> 'no',
		'search' 		=> array(),
		'results' 		=> array(),
	);
	
	$flag = false;
	
	if(!is_array($dataPageSettings)){
		$dataPageSettings = $settingsDefaults;
		$flag = true;
