function fm_form_have_items(){
	global $fm_display;
	$done = false;
	$index = $fm_display->currentItemIndex;
	
	while(!$done) {
		$index++;
		if( $index >= sizeof($fm_display->currentFormInfo['items']) ) {
			return false;
		}
		
		$item = $fm_display->currentFormInfo['items'][$index];
		if( $item['set'] == 0 ) {
			$done = true;
			$fm_display->nextItemIndex = $index;
		}
	}
	return true;
}

