add_action( 'admin_menu', 'fm_setupAdminMenu' );
function fm_setupAdminMenu() {
	global $fmdb;
	
	$pages[] = add_object_page(
		__("Forms", 'wordpress-form-manager'), 
		__("Forms", 'wordpress-form-manager'),
		apply_filters( 'fm_main_capability', 'manage_options' ), 
		'fm-admin-main', 
		'fm_showMainPage',
