add_shortcode( get_option( 'fm-shortcode' ), 'fm_shortcodeHandler' );
function fm_shortcodeHandler( $atts ) {
	if ( !isset( $atts[ 0 ] ) ) {
		return sprintf(
			__("Form Manager: shortcode must include a form slug.  For example, something like '%s'", 'wordpress-form-manager'), 
			"[form form-1]"
			);
	}
	return fm_doFormBySlug( $atts[ 0 ] );
}

add_shortcode( get_option( 'fm-data-shortcode' ), 'fm_dataShortcodeHandler' );
function fm_dataShortcodeHandler( $atts ) {
	if ( !isset( $atts[ 0 ] ) ) {
		return sprintf(
			__("Form Manager: shortcode must include a form slug.  For example, something like '%s'", 'wordpress-form-manager'), 
			"[formdata form-1]"
			);
	}
	$formSlug = $atts[ 0 ];
	
	$options = $atts;
	
	$showTable = false;
	foreach ( $atts as $att )
		if ( $att == 'table' )
			$showTable = true;
			
	$atts = shortcode_atts(
		array(
			'orderby' => 'timestamp',
			'order' => 'desc',
			'dataperpage' => 30,
			'template' => '',
			'row_height' => 'auto',
			'row_class' => '',
			'col_width' => 'auto',
			'col_class' => '',
			'show' => '',
			'hide' => '',
			'showprivate' => '',
			), 
		$atts
		);
		
	if ( $showTable ) {
		return fm_doDataTableBySlug(
			$formSlug,
			$atts[ 'template' ],
			$atts[ 'orderby' ],
			$atts[ 'order' ],
			$atts[ 'dataperpage' ],
			$options
			);
	} else {
		return fm_doDataListBySlug(
			$formSlug,
			$atts[ 'template' ],
			$atts[ 'orderby' ],
			$atts[ 'order' ],
			$atts[ 'dataperpage' ],
			$options
			);
	}
	
}

